import { useState, useCallback } from 'react'; 
import Gallery from 'react-photo-gallery';
import {userImages} from './images'; 
import {NavBar} from './home'; 
import Carousel, { Modal, ModalGateway }  from 'react-images'; 
import "./gallery.css"; 

export default function MyGallery() { 
    return (
        <> 
            <div className='main'> 
                <NavBar></NavBar>
                <Title></Title> 
                <MainContent></MainContent> 
            </div> 
        </>); 
} 

function Title(){ 
    const [file,setFile] = useState(null); 
    const [message,setMessage] = useState(null); 
    async function uploadImage(){ 
        console.log(file); 
        if (file) {
            const formData = new FormData();
            formData.append('file', file);

            try {
                const response = await fetch("http://localhost:8080/user/"+localStorage.getItem('username')+"/images", {method: 'POST',
                                                                                                                        body: formData
                });

                if (response.status==200) { 
                    setMessage('File uploaded successfully!');
                } else {
                    setMessage('File upload failed.');
                }
            } catch (error) {
                setMessage('An error occurred.');
                console.error('Error:', error); 
            }
        }
    }
    return (<> 
                <header className="hero">My Gallery</header> 
                <div> 
                    <input type="file" name="filename" accept=".jpg, .jpeg, .png" onChange={(e)=>setFile(e.target.files[0])}/> 
                    <button type='submit' onClick={uploadImage}>Upload</button> 
                </div>
            </>); 
} 

function MainContent(){ 
    const images = userImages(); 
    console.log(images); 
    const [Caropened,setCaropened] = useState(false); 
    const [Currimg, setCurrimg] = useState(0); 
    console.log(images); 
    function imageRenderer({ index=0, key='key', photo={}}){ 
        return ( 
        <img 
            className='thumbnail'
            index={index} 
            key={key}
            src={photo.src} 
            width={photo.width} 
            height={photo.height} 
            onClick={()=>openLightbox(index,photo)} 
        /> 
    );} 

    function openLightbox(index,photo){ 
        setCaropened(true); 
        setCurrimg(index); 
    } 
    function closeLightbox(){ 
        setCaropened(false); 
    }

    if (images.length==0){ 
        return (<> 
            <div className='content'> 
                <h2>Upload Your First Picture</h2> 
            </div> 
        </>); 
    } 
    return (<> 
                <div className='content'> 
                    <Gallery photos={images} renderImage={imageRenderer} direction='row'/>
                    <ModalGateway>
                    {Caropened ? (
                    <Modal onClose={closeLightbox}>
                        <Carousel
                        currentIndex={Currimg} 
                        views={images.map(x => ({
                            ...x,
                            srcset: x.srcSet,
                        }))} 
                        />
                    </Modal>
                    ) : null}
                </ModalGateway> 
                </div> 
            </>); 
}
