import { useState } from 'react';
import {useNavigate} from "react-router-dom"; 
import {NavBar} from './home'; 
import "./create.css"; 
export default function Create(){ 
    return ( 
        <>
            <div className='login_main'> 
                <NavBar></NavBar> 
                <Form></Form> 
            </div> 
        </>
        
    ); 
} 
function Form() { 
    const [Username,setUsername] = useState(null); 
    const [Password,setPassword] = useState(null); 
    const [PasswordConf,setPasswordConf] = useState(null); 
    const [Failed,setFailed] = useState(false); 
    const navigate = useNavigate(); 
    function createHandler(){ 
        if (PasswordConf != Password) { 
            const flags = Failed ? "Failed Auth!" : "Please create an account below";   
        } 
        fetch("http://localhost:8080/user", {method:"POST", 
                                            headers: {'content-type':'application/json'}, 
                                            body: JSON.stringify({"username": Username,"password":Password }) 
            })  
            .then((response) => response.json()) 
            .then((json) => console.log(json)); 
    } 
    const flags = Failed ? "Failed Auth!" : "Please create an account below";   
    return ( 
        <> 
            <div className="credentials-form"> 
                <p className='messages'>{flags}</p> 
                <div className="submit-container"> 
                    <input type="email" id="user" placeholder="Username" onChange={(e) => setUsername(e.target.value)}/> 
                    <input type="password" id="pass" placeholder="Password" onChange={(e) => setPassword(e.target.value)}/> 
                    <input type="password" id="pass" placeholder="Confirm Password" onChange={(e) => setPasswordConf(e.target.value)}/> 
                    <div className='buttons'> 
                        <button type="submit" onClick={createHandler}>Create Account</button> 
                    </div> 
                </div> 
            </div> 
        </> 
    ); 
}
