import { useState, useCallback, useContext } from 'react'; 
import { useNavigate } from "react-router-dom"; 
import { useAuth } from './authContext'; 

import "./home.css"; 
export default function Home() { 
    return ( 
        <> 
        <div className='main'> 
            <NavBar/> 
            <Welcome/> 
        </div> 
        </>); 
} 

function Welcome(){ 
    const navigate = useNavigate(); 
    return (
        <div className='welc'> 
            <div className='side_by_side'> 
                <div className='moto'> 
                    <h1>Welcome to ImageCloud!</h1> 
                    <h3>A simple solution to take and store your pictures to the cloud and access them anywhere. Available on the website and on Android. Click to create an account or login to start.</h3>
                    <div className='moto_buttons'> 
                        <button onClick={()=>{navigate('/login')}}>Login</button> 
                        <button onClick={()=>{navigate('/create')}}>Create Account</button> 
                    </div> 
                </div> 
                <div className='welc_images'> 
                    <img src='home.png'/> 
                </div>
            </div> 
        </div>
    ); 
}


function SignoutDropdown(){ 
    const navigate = useNavigate(); 
    const { isLoggedIn, logout } = useAuth(); 
    function handleSignout(){ 
        localStorage.removeItem('username'); 
        localStorage.removeItem('session_token'); 
        logout(); 
        navigate('/login'); 
    } 
    if (!isLoggedIn){ 
        return (
            <> 
                <select className='web_select' disabled> 
                    <option disabled selected hidden className='disabled_option'>Guest Browsing</option> 
                </select> 
            </>); 
    } 
    return ( 
        <> 
            <select className='web_select' onChange={handleSignout}> 
                <option disabled selected hidden className='disabled_option'>{localStorage.getItem('username')}</option> 
                <option>Sign Out</option> 
            </select> 
 
        </>
    ); 
}

export function NavBar(){ 
    const navigate = useNavigate(); 

    return( 
        <nav className='navbar'> 
            <img onClick={()=>navigate('/')} src='icon.png'/> 
            <SignoutDropdown/> 
            <div className='nav_button_container'> 
                <button onClick={()=>navigate('/')}>Home</button> 
                <button onClick={()=>navigate('/gallery')}>Gallery</button> 
                <button onClick={()=>navigate('/create')}>Create Account</button> 
                <button onClick={()=>navigate('/login')}>Login</button> 
            </div> 
        </nav>
    ); 
}
