import { useState, useCallback } from 'react'; 
import { useNavigate, Link } from "react-router-dom"; 
import Gallery from 'react-photo-gallery';
import {userImages} from './images'; 
import NavBar from './home'; 
import Carousel, { Modal, ModalGateway }  from 'react-images'; 

export default function MyGallery() { 
    return (
        <> 
        <div className='main'> 
            <Title></Title> 
            <MainContent></MainContent> 
        </div> 
        </>); 
} 

function Title(){ 
    const navigate = useNavigate(); 
    function handleClick() { 
        navigate("/login"); 
    } 
    return (<> 
                <div className='top'> 
                    <button onClick={handleClick}>Go to Login</button>  
                    <header className="hero">Your Gallery</header> 
                </div> 
            </>); 
} 

function MainContent(){ 
    const images = userImages(); 
    const [Caropened,setCaropened] = useState(false); 
    const [Currimg, setCurrimg] = useState(0); 
    console.log(images); 
    function imageRenderer({ index=0, key='key', photo={}}){ 
        return ( 
        <img 
            className='thumbnail'
            index={index} 
            key={key}
            src={photo.src} 
            width={photo.width} 
            height={photo.height} 
            onClick={()=>openLightbox(index,photo)} 
        /> 
    );} 

    function openLightbox(index,photo){ 
        setCaropened(true); 
        setCurrimg(index); 
    } 
    function closeLightbox(){ 
        setCaropened(false); 
    }

    if (images.length==0){ 
        return (<> 
            <div className='content'> 
                <h2>Upload Your First Picture</h2> 
            </div>
        </>); 
    }
    return (<> 
                <div className='content'> 
                    <Gallery photos={images} renderImage={imageRenderer} direction='row'/>
                    <ModalGateway>
                    {Caropened ? (
                    <Modal onClose={closeLightbox}>
                        <Carousel
                        currentIndex={Currimg} 
                        views={images.map(x => ({
                            ...x,
                            srcset: x.srcSet,
                        }))} 
                        />
                    </Modal>
                    ) : null}
                </ModalGateway>
                </div> 
            </>); 
}
