import { useState, useRef, useEffect } from 'react'; 
import { useNavigate } from "react-router-dom"; 
import { useAuth } from './authContext'; 

import "./home.css"; 
export default function Home() { 
    return ( 
        <> 
        <div className='main'> 
            <NavBar page={0}/> 
            <Welcome/> 
        </div> 
        </>); 
} 

function Welcome(){ 
    const navigate = useNavigate(); 
    return (
        <div className='welc'> 
            <div className='side_by_side'> 
                <div className='moto'> 
                    <h1>Welcome to ImageCloud!</h1> 
                    <h3>A simple solution to take and store your pictures to the cloud and access them anywhere. Available on the website and on Android. Click to create an account or login to start.</h3>
                    <div className='moto_buttons'> 
                        <button onClick={()=>{navigate('/login')}}>Login</button> 
                        <button onClick={()=>{navigate('/create')}}>Create Account</button> 
                    </div> 
                </div> 
                <div className='welc_images'> 
                    <img src='home.png'/> 
                </div>
            </div> 
        </div>
    ); 
}


function SignoutDropdown(){ 
    const navigate = useNavigate(); 
    const { isLoggedIn, logout } = useAuth(); 
    const [openDrop, setOpenDrop] = useState(false); 
    const dropdownRef = useRef(null); 
    function handleSignout(){ 
        localStorage.removeItem('username'); 
        localStorage.removeItem('session_token'); 
        setOpenDrop(false); 
        logout(); 
        navigate('/login'); 
    } 

    function toggleSidebar(){ 
        // const drop = document.getElementById('hide'); 
        // const dropBtn = document.getElementById('userBtn'); 
        // if(drop.style.display == 'none'){ 
        //     dropBtn.style.backgroundColor = '#0072ff'; 
        //     drop.style.display = 'block'; 
        //     return; 
        // } 
        // dropBtn.style.backgroundColor =  '#4e54c8'; 
        // drop.style.display = 'none';     
        setOpenDrop(!openDrop); 
    } 

    const handleClickOutside = (event) => {
        if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
            setOpenDrop(false);
        }
      };
    
      useEffect(() => {
        document.addEventListener('mousedown', handleClickOutside);
        return () => {
          document.removeEventListener('mousedown', handleClickOutside);
        };
      }, []);

    if (!isLoggedIn){ 
        return (
            <> 
           <div class="dropdown" ref={dropdownRef}>
                <button class="dropbtn">Guest User</button>
                <div class={openDrop?"dropdown-content":'dropdown-content-hide'} id='hide'> 
                    <a onClick={handleSignout}>Sign Out</a> 
                </div>
            </div> 
            </>); 
    } 
    return ( 
        <> 
        <div onClick={toggleSidebar} class="dropdown" ref={dropdownRef}>
            <button id='userBtn' class="dropbtn">{localStorage.getItem('username')}</button>
            <div class={openDrop?"dropdown-content":'dropdown-content-hide'} id='hide'> 
                <a onClick={handleSignout}>Sign Out</a> 
            </div>
        </div> 
        </>
    ); 
}

export function NavBar(props){ 
    const navigate = useNavigate(); 
    return( 
        <div className='navbar'> 
        <nav className='nav_sides_container'> 
            <img onClick={()=>navigate('/')} src='icon.png'/> 
            <SignoutDropdown/> 
        </nav> 
        <div className='nav_middle_container'> 
            <div className='nav_button_container'> 
                <button onClick={()=>navigate('/')}>Home</button> 
                <button onClick={()=>navigate('/gallery')}>Gallery</button> 
                <button onClick={()=>navigate('/create')}>Create Account</button> 
                <button onClick={()=>navigate('/login')}>Login</button> 
            </div> 
        </div> 
    </div>
    ); 
}
